mapper.delete:
DELETE FROM addressBook
WHERE addressNum = #{addressNum} and userId =
#{userId}

mapper.select:
SELECT ob.orderNum, r.restaurantName, p.price total_price, os.statusName, ob.userId, ob.order_date, ob.restaurantNum, rv.content FROM order_bundle ob JOIN restaurant r ON ob.restaurantNum = r.restaurantNum JOIN payment p ON ob.orderNum = p.orderNum JOIN order_status os ON ob.orderNum = os.orderNum LEFT OUTER JOIN review rv ON ob.orderNum = rv.orderNum
AND ob.userId = #{userId}
ORDER BY ob.order_date DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY
